Bash(python -c "
import requests
import json

base_url = ''https://www.amazon.jobs/en/search.json''

headers = {
    ''User-Agent'': ''Mozilla/5.0 \(Windows NT 10.0; Win64; x64\) AppleWebKit/537.36'',
    ''Accept'': ''application/json'',
    ''Accept-Encoding'': ''gzip, deflate'',
    ''Referer'': ''https://www.amazon.jobs/en/search'',
}

# Test different parameter combinations
test_configs = [
    {
        ''name'': ''Test 1: country[]=DEU'',
        ''params'': {
            ''base_query'': ''machine learning'',
            ''country[]'': ''DEU'',
            ''result_limit'': 10
        }
    },
    {
        ''name'': ''Test 2: loc_query=Germany'',
        ''params'': {
            ''base_query'': ''machine learning'',
            ''loc_query'': ''Germany'',
            ''result_limit'': 10
        }
    },
    {
        ''name'': ''Test 3: normalized_country_code[]=DEU'',
        ''params'': {
            ''base_query'': ''machine learning'',
            ''normalized_country_code[]'': ''DEU'',
            ''result_limit'': 10
        }
    },
]

print\(''=''*80\)
print\(''Testing Amazon Jobs API Parameters for Germany Filtering''\)
print\(''=''*80\)

for config in test_configs:
    print\(f""\\n{config[''name'']}""\)
    print\(''-'' * 80\)

    try:
        response = requests.get\(base_url, params=config[''params''], headers=headers\)
        response.raise_for_status\(\)
        data = response.json\(\)

        jobs = data.get\(''jobs'', []\)
        total_hits = data.get\(''hits'', 0\)

        print\(f''Total hits: {total_hits}''\)
        print\(f''Jobs returned: {len\(jobs\)}''\)

        if jobs:
            print\(''\\nFirst 3 job locations:''\)
            for i, job in enumerate\(jobs[:3], 1\):
                location = job.get\(''location'', ''N/A''\)
                country_code = job.get\(''country_code'', ''N/A''\)
                city = job.get\(''city'', ''N/A''\)
                title = job.get\(''title'', ''N/A''\)[:60]
                print\(f''  {i}. {title}''\)
                print\(f''     Location: {location} | Country: {country_code} | City: {city}''\)

    except Exception as e:
        print\(f''ERROR: {e}''\)

print\(''\\n'' + ''=''*80\)
")
